write_global_param <- function(path_sim, syear, eyear, timestep = 24, output_force = TRUE, model = "hbv") {
  
  library(GetoptLong)
  
  filename <- file.path(path_sim, "/params/global_param")
  
  qqcat("#######################################################################\n", file = filename, append = FALSE)
  qqcat("# VIC Model Parameters - 4.1.x\n", file = filename, append = TRUE)
  qqcat("#######################################################################\n", file = filename, append = TRUE)
  qqcat("# $Id: global.param.sample,v 5.7.2.27 2012/02/07 19:03:02 vicadmin Exp $\n", file = filename, append = TRUE)
  qqcat("#######################################################################\n", file = filename, append = TRUE)
  qqcat("# Simulation Parameters\n", file = filename, append = TRUE)
  qqcat("#######################################################################\n", file = filename, append = TRUE)
  qqcat("NLAYER		3	# number of soil layers\n", file = filename, append = TRUE)
  qqcat("NODES		3	# number of soil thermal nodes \n", file = filename, append = TRUE)
  qqcat("TIME_STEP 	@{timestep}	# model time step in hours (set to 24 if FULL_ENERGY = FALSE, set to < 24 if FULL_ENERGY = TRUE)\n", file = filename, append = TRUE)
  qqcat("SNOW_STEP	@{timestep}	# time step in hours for which to solve the snow model (should = TIME_STEP if TIME_STEP < 24)\n", file = filename, append = TRUE)
  qqcat("STARTYEAR	@{syear}	# year model simulation starts\n", file = filename, append = TRUE)
  qqcat("STARTMONTH	01	# month model simulation starts\n", file = filename, append = TRUE)
  qqcat("STARTDAY	01 	# day model simulation starts\n", file = filename, append = TRUE)
  qqcat("STARTHOUR	00	# hour model simulation starts\n", file = filename, append = TRUE)
  qqcat("ENDYEAR 	@{eyear}	# year model simulation ends\n", file = filename, append = TRUE)
  qqcat("ENDMONTH	12	# month model simulation ends\n", file = filename, append = TRUE)
  qqcat("ENDDAY		31	# day model simulation ends\n", file = filename, append = TRUE)
  qqcat("FULL_ENERGY 	FALSE	# TRUE = calculate full energy balance; FALSE = compute water balance only\n", file = filename, append = TRUE)
  qqcat("FROZEN_SOIL	FALSE	# TRUE = calculate frozen soils\n", file = filename, append = TRUE)
  qqcat("\n", file = filename, append = TRUE)
  qqcat("#######################################################################\n", file = filename, append = TRUE)
  qqcat("# Soil Temperature Parameters\n", file = filename, append = TRUE)
  qqcat("# VIC will choose appropriate values for QUICK_FLUX and IMPLICIT depending on values of FULL_ENERGY and FROZEN_SOIL; the user should only need to override VICs choices in special cases.\n", file = filename, append = TRUE)
  qqcat("# The other options in this section are only applicable when FROZEN_SOIL is TRUE and their values depend on the appliqqcation.\n", file = filename, append = TRUE)
  qqcat("#######################################################################\n", file = filename, append = TRUE)
  qqcat("#QUICK_FLUX	FALSE	# TRUE = use simplified ground heat flux method of Liang et al (1999); FALSE = use finite element method of Cherkauer et al (1999)\n", file = filename, append = TRUE)
  qqcat("#IMPLICIT	TRUE	# TRUE = use implicit solution for soil heat flux equation of Cherkauer et al (1999), otherwise uses original explicit solution.\n", file = filename, append = TRUE)
  qqcat("#QUICK_SOLVE	FALSE	# TRUE = Use Liang et al., 1999 formulation for iteration, but explicit finite difference method for final step.\n", file = filename, append = TRUE)
  qqcat("#NO_FLUX		FALSE	# TRUE = use no flux lower boundary for ground heat flux computation; FALSE = use constant flux lower boundary condition.  If NO_FLUX = TRUE, QUICK_FLUX MUST = FALSE\n", file = filename, append = TRUE)
  qqcat("#EXP_TRANS	FALSE	# TRUE = exponentially distributes the thermal nodes in the Cherkauer et al. (1999) finite difference algorithm, otherwise uses linear distribution\n", file = filename, append = TRUE)
  qqcat("#GRND_FLUX_TYPE	GF_410	# Options for ground flux:\n", file = filename, append = TRUE)
  qqcat("#			# GF_406 = use (flawed) formulas for ground flux, deltaH, and fusion from VIC 4.0.6 and earlier;\n", file = filename, append = TRUE)
  qqcat("#			# GF_410 = use formulas from VIC 4.1.0 (ground flux, deltaH, and fusion are correct; deltaH and fusion ignore surf_atten);\n", file = filename, append = TRUE)
  qqcat("#			# Default = GF_410\n", file = filename, append = TRUE)
  qqcat("#TFALLBACK	TRUE	# TRUE = when temperature iteration fails to converge, use previous time steps T value\n", file = filename, append = TRUE)
  qqcat("\n", file = filename, append = TRUE)
  qqcat("#######################################################################\n", file = filename, append = TRUE)
  qqcat("# Precip (Rain and Snow) Parameters\n", file = filename, append = TRUE)
  qqcat("# Generally these default values do not need to be overridden\n", file = filename, append = TRUE)
  qqcat("#######################################################################\n", file = filename, append = TRUE)
