  qqcat("#SNOW_DENSITY	DENS_BRAS	# DENS_BRAS = use traditional VIC algorithm taken from Bras, 1990; DENS_SNTHRM = use algorithm taken from SNTHRM model.\n", file = filename, append = TRUE)
  qqcat("#BLOWING		FALSE	# TRUE = compute evaporative fluxes due to blowing snow\n", file = filename, append = TRUE)
