  qqcat("#DIST_PRCP	FALSE	# TRUE = use distributed precipitation\n", file = filename, append = TRUE)
  qqcat("#PREC_EXPT	0.6	# exponent for use in distributed precipitation eqn (only used if DIST_PRCP is TRUE)\n", file = filename, append = TRUE)
  qqcat("#CORRPREC	FALSE	# TRUE = correct precipitation for gauge underqqcatch\n", file = filename, append = TRUE)
  qqcat("#MAX_SNOW_TEMP	0.5	# maximum temperature (C) at which snow can fall\n", file = filename, append = TRUE)
  qqcat("#MIN_RAIN_TEMP	-0.5	# minimum temperature (C) at which rain can fall\n", file = filename, append = TRUE)
  qqcat("\n", file = filename, append = TRUE)
  qqcat("#######################################################################\n", file = filename, append = TRUE)
  qqcat("# Turbulent Flux Parameters\n", file = filename, append = TRUE)
  qqcat("# Generally these default values do not need to be overridden\n", file = filename, append = TRUE)
  qqcat("#######################################################################\n", file = filename, append = TRUE)
  qqcat("#MIN_WIND_SPEED	0.1	# minimum allowable wind speed (m/s)\n", file = filename, append = TRUE)
  qqcat("#AERO_RESIST_CANSNOW	AR_406_FULL	# Options for aerodynamic resistance in snow-filled canopy:\n", file = filename, append = TRUE)
  qqcat("#			# AR_406 	= multiply by 10 for latent heat but do NOT multiply by 10 for sensible heat and do NOT apply stability correction (as in VIC 4.0.6); when no snow in canopy, use surface aero_resist for ET.\n", file = filename, append = TRUE)
  qqcat("#			# AR_406_LS 	= multiply by 10 for latent heat AND sensible heat and do NOT apply stability correction; when no snow in canopy, use surface aero_resist for ET.\n", file = filename, append = TRUE)
  qqcat("#			# AR_406_FULL 	= multiply by 10 for latent heat AND sensible heat and do NOT apply stability correction; additionally, always use overstory aero_resist for ET (as in 4.1.0).\n", file = filename, append = TRUE)
  qqcat("#			# AR_410 	= apply stability correction but do NOT multiply by 10 (as in VIC 4.1.0); additionally, always use overstory aero_resist for ET (as in 4.1.0).\n", file = filename, append = TRUE)
  qqcat("#			# Default 	= AR_406_FULL\n", file = filename, append = TRUE)
  qqcat("\n", file = filename, append = TRUE)
  qqcat("#######################################################################\n", file = filename, append = TRUE)
  qqcat("# Meteorological Forcing Disaggregation Parameters\n", file = filename, append = TRUE)
  qqcat("# Generally these default values do not need to be overridden\n", file = filename, append = TRUE)
  qqcat("#######################################################################\n", file = filename, append = TRUE)
  qqcat("#PLAPSE		TRUE	# This controls how VIC computes air pressure when air pressure is not supplied as an input forcing: TRUE = set air pressure to sea level pressure, lapsed to grid cell average elevation; FALSE = set air pressure to constant 95.5 kPa (as in all versions of VIC pre-4.1.1)\n", file = filename, append = TRUE)
  qqcat("#SW_PREC_THRESH		0	# Minimum daily precip [mm] that can cause dimming of incoming shortwave; default = 0.\n", file = filename, append = TRUE)
  qqcat("MTCLIM_SWE_CORR	FALSE    # This controls VICs estimates of incoming shortwave in the presence of snow; TRUE = adjust incoming shortwave for snow albedo effect; FALSE = do not adjust shortwave; default = TRUE\n", file = filename, append = TRUE)
  qqcat("#VP_ITER		VP_ITER_ALWAYS	# This controls VICs iteration between estimates of shortwave and vapor pressure:\n", file = filename, append = TRUE)
  qqcat("#			# VP_ITER_NEVER = never iterate; make estimates separately\n", file = filename, append = TRUE)
  qqcat("#			# VP_ITER_ALWAYS = always iterate once\n", file = filename, append = TRUE)
  qqcat("#			# VP_ITER_ANNUAL = iterate once for arid climates based on annual Precip/PET ratio\n", file = filename, append = TRUE)
  qqcat("#			# VP_ITER_CONVERGE = iterate until shortwave and vp stabilize\n", file = filename, append = TRUE)
  qqcat("#			# default = VP_ITER_ALWAYS\n", file = filename, append = TRUE)
  qqcat("#VP_INTERP	TRUE	# This controls sub-daily humidity estimates; TRUE = interpolate daily VP estimates linearly between sunrise of one day to the next; FALSE = hold VP constant for entire day\n", file = filename, append = TRUE)
  qqcat("#LW_TYPE		LW_TVA	# This controls the algorithm used to estimate clear-sky longwave radiation:\n", file = filename, append = TRUE)
  qqcat("#			# LW_TVA = Tennessee Valley Authority algorithm (1972)\n", file = filename, append = TRUE)
  qqcat("#			# other options listed in vicNl_def.h\n", file = filename, append = TRUE)
  qqcat("#			# default = LW_TVA (this was also the traditional VIC algorithm)\n", file = filename, append = TRUE)
  qqcat("#LW_CLOUD	LW_CLOUD_DEARDORFF	# This controls the algorithm used to estimate the influence of clouds on total longwave:\n", file = filename, append = TRUE)
  qqcat("#			# LW_CLOUD_BRAS = method from Bras textbook (this was the traditional VIC algorithm)\n", file = filename, append = TRUE)
  qqcat("#			# LW_CLOUD_DEARDORFF = method of Deardorff (1978)\n", file = filename, append = TRUE)
  qqcat("#			# default = LW_CLOUD_DEARDORFF\n", file = filename, append = TRUE)
  qqcat("OUTPUT_FORCE	@{output_force}\n", file = filename, append = TRUE)
  qqcat("\n", file = filename, append = TRUE)
  qqcat("#######################################################################\n", file = filename, append = TRUE)
  qqcat("# Miscellaneous Simulation Parameters\n", file = filename, append = TRUE)
  qqcat("# Generally these default values do not need to be overridden\n", file = filename, append = TRUE)
  qqcat("#######################################################################\n", file = filename, append = TRUE)
