  qqcat("#######################################################################\n", file = filename, append = TRUE)
  qqcat("# State Files and Parameters\n", file = filename, append = TRUE)
  qqcat("#######################################################################\n", file = filename, append = TRUE)
  qqcat("#INIT_STATE	/home/jmg/VIC-master/12.70/params/vic/state/STEHE.state_19481231	# Initial state path/file\n", file = filename, append = TRUE)
  qqcat("#STATENAME	/home/jmg/VIC-master/12.70/state/STEHE.state	# Output state file path/prefix.  The date (STATEYEAR,STATEMONTH,STATEDAY) will be appended to the prefix automatically in the format yyyymmdd.\n", file = filename, append = TRUE)
