  qqcat("#STATEMONTH	12	# month to save model state\n", file = filename, append = TRUE)
  qqcat("#STATEDAY	31	# day to save model state\n", file = filename, append = TRUE)
  qqcat("#BINARY_STATE_FILE       FALSE	# TRUE if state file should be binary format; FALSE if ascii\n", file = filename, append = TRUE)
