  qqcat("#			SIGNED/UNSIGNED	SCALE_FACTOR\n", file = filename, append = TRUE)
  qqcat("#		For example (BINARY):\n", file = filename, append = TRUE)
  qqcat("#			FORCE_TYPE	PREC	UNSIGNED	40\n", file = filename, append = TRUE)
  qqcat("#		or (ASCII):\n", file = filename, append = TRUE)
