  qqcat("\n", file = filename, append = TRUE)
  qqcat("#FORCING1	/home/jmg/VIC-master/testdata2/forcing/data_	# Forcing file path and prefix\n", file = filename, append = TRUE)
  qqcat("#N_TYPES     4\n", file = filename, append = TRUE)
  qqcat("#FORCE_TYPE  PREC\n", file = filename, append = TRUE)
  qqcat("#FORCE_TYPE  TMAX\n", file = filename, append = TRUE)
  qqcat("#FORCE_TYPE  TMIN\n", file = filename, append = TRUE)
  qqcat("#FORCE_TYPE  WIND\n", file = filename, append = TRUE)
  qqcat("#FORCE_FORMAT    ASCII\n", file = filename, append = TRUE)
