  qqcat("#FORCEMONTH  09\n", file = filename, append = TRUE)
  qqcat("#FORCEDAY    01\n", file = filename, append = TRUE)
  qqcat("#FORCEHOUR   00\n", file = filename, append = TRUE)
  qqcat("#GRID_DECIMAL	4\n", file = filename, append = TRUE)
  qqcat("#FORCING2    FALSE\n", file = filename, append = TRUE)
  qqcat("\n", file = filename, append = TRUE)
  qqcat("#FORCING1	/home/jmg/Projects/VIC_MTCLIM/test_case/forcing/data_	# Forcing file path and prefix\n", file = filename, append = TRUE)
  qqcat("#FORCE_FORMAT	ASCII	# BINARY or ASCII\n", file = filename, append = TRUE)
  qqcat("#N_TYPES		4	# Number of variables (columns)\n", file = filename, append = TRUE)
  qqcat("#FORCE_TYPE	PREC\n", file = filename, append = TRUE)
  qqcat("#FORCE_TYPE	TMAX\n", file = filename, append = TRUE)
  qqcat("#FORCE_TYPE	TMIN\n", file = filename, append = TRUE)
