  qqcat("#ALMA_INPUT	FALSE	# TRUE = ALMA-compliant input variable units; FALSE = standard VIC units\n", file = filename, append = TRUE)
  qqcat("#FORCING2    FALSE\n", file = filename, append = TRUE)
  qqcat("\n", file = filename, append = TRUE)
