  qqcat("FORCING1	@{path_sim}/forcing/data_\n", file = filename, append = TRUE)
  qqcat("GRID_DECIMAL	5\n", file = filename, append = TRUE)
  qqcat("N_TYPES		4\n", file = filename, append = TRUE)
