  qqcat("#\n", file = filename, append = TRUE)
  qqcat("#   OUTFILE       <prefix>        <nvars>\n", file = filename, append = TRUE)
  qqcat("#   OUTVAR        <varname>       [<format>        <type>  <multiplier>]\n", file = filename, append = TRUE)
  qqcat("#   OUTVAR        <varname>       [<format>        <type>  <multiplier>]\n", file = filename, append = TRUE)
  qqcat("#   OUTVAR        <varname>       [<format>        <type>  <multiplier>]\n", file = filename, append = TRUE)
  qqcat("#\n", file = filename, append = TRUE)
  qqcat("#\n", file = filename, append = TRUE)
  qqcat("# where\n", file = filename, append = TRUE)
  qqcat("#   <n_outfiles> = number of output files\n", file = filename, append = TRUE)
  qqcat("#   <prefix>     = name of the output file, NOT including latitude\n", file = filename, append = TRUE)
  qqcat("#                  and longitude\n", file = filename, append = TRUE)
  qqcat("#   <nvars>      = number of variables in the output file\n", file = filename, append = TRUE)
  qqcat("#   <varname>    = name of the variable (this must be one of the\n", file = filename, append = TRUE)
  qqcat("#                  output variable names listed in vicNl_def.h.)\n", file = filename, append = TRUE)
  qqcat("#   <format>     = (for ascii output files) fprintf format string,\n", file = filename, append = TRUE)
  qqcat("#                  e.g.\n", file = filename, append = TRUE)
  qqcat("#                    %.4f = floating point with 4 decimal places\n", file = filename, append = TRUE)
  qqcat("#                    %.7e = scientific notation w/ 7 decimal places\n", file = filename, append = TRUE)
  qqcat("#                    *    = use the default format for this variable\n", file = filename, append = TRUE)
  qqcat("#\n", file = filename, append = TRUE)
  qqcat("#   <format>, <type>, and <multiplier> are optional.  For a given\n", file = filename, append = TRUE)
  qqcat("#   variable, you can specify either NONE of these, or ALL of\n", file = filename, append = TRUE)
  qqcat("#   these.  If these are omitted, the default values will be used.\n", file = filename, append = TRUE)
  qqcat("#\n", file = filename, append = TRUE)
  qqcat("#   <type>       = (for binary output files) data type code.\n", file = filename, append = TRUE)
  qqcat("#                  Must be one of:\n", file = filename, append = TRUE)
  qqcat("#                    OUT_TYPE_DOUBLE = double-precision floating point\n", file = filename, append = TRUE)
  qqcat("#                    OUT_TYPE_FLOAT  = single-precision floating point\n", file = filename, append = TRUE)
  qqcat("#                    OUT_TYPE_INT    = integer\n", file = filename, append = TRUE)
  qqcat("#                    OUT_TYPE_USINT  = unsigned short integer\n", file = filename, append = TRUE)
  qqcat("#                    OUT_TYPE_SINT   = short integer\n", file = filename, append = TRUE)
  qqcat("#                    OUT_TYPE_CHAR   = char\n", file = filename, append = TRUE)
  qqcat("#                    *               = use the default type\n", file = filename, append = TRUE)
  qqcat("#   <multiplier> = (for binary output files) factor to multiply\n", file = filename, append = TRUE)
  qqcat("#                  the data by before writing, to increase precision.\n", file = filename, append = TRUE)
  qqcat("#                    *    = use the default multiplier for this variable\n", file = filename, append = TRUE)
  qqcat("#\n", file = filename, append = TRUE)
  qqcat("#######################################################################\n", file = filename, append = TRUE)
  
  if (output_force && model == "hbv") {
    qqcat("\n", file = filename, append = TRUE)
    qqcat("N_OUTFILES    1\n", file = filename, append = TRUE)
    qqcat("\n", file = filename, append = TRUE)
    qqcat("OUTFILE       metdata        8 \n", file = filename, append = TRUE)
    qqcat("\n", file = filename, append = TRUE)
    qqcat("OUTVAR		OUT_PREC	* OUT_TYPE_USINT	40\n", file = filename, append = TRUE)
    qqcat("OUTVAR		OUT_AIR_TEMP	* OUT_TYPE_SINT		100\n", file = filename, append = TRUE)
    qqcat("OUTVAR		OUT_SHORTWAVE	* OUT_TYPE_USINT	50\n", file = filename, append = TRUE)
    qqcat("OUTVAR		OUT_LONGWAVE	* OUT_TYPE_USINT	80\n", file = filename, append = TRUE)
    qqcat("OUTVAR		OUT_PRESSURE	* OUT_TYPE_USINT	100\n", file = filename, append = TRUE)
    qqcat("OUTVAR		OUT_QAIR	* OUT_TYPE_USINT	100000\n", file = filename, append = TRUE)
    qqcat("OUTVAR		OUT_VP		* OUT_TYPE_USINT	100\n", file = filename, append = TRUE)
    qqcat("OUTVAR		OUT_WIND	* OUT_TYPE_USINT	100\n", file = filename, append = TRUE)
  }

  if (output_force && model == "fsm") {
    qqcat("\n", file = filename, append = TRUE)
    qqcat("N_OUTFILES    1\n", file = filename, append = TRUE)
    qqcat("\n", file = filename, append = TRUE)
    qqcat("OUTFILE       metdata        8 \n", file = filename, append = TRUE)
    qqcat("\n", file = filename, append = TRUE)
    qqcat("OUTVAR		OUT_RAINF	* OUT_TYPE_USINT	40\n", file = filename, append = TRUE)
    qqcat("OUTVAR		OUT_SNOWF	* OUT_TYPE_USINT	40\n", file = filename, append = TRUE)
    qqcat("OUTVAR		OUT_AIR_TEMP	* OUT_TYPE_SINT		100\n", file = filename, append = TRUE)
    qqcat("OUTVAR		OUT_SHORTWAVE	* OUT_TYPE_USINT	50\n", file = filename, append = TRUE)
    qqcat("OUTVAR		OUT_LONGWAVE	* OUT_TYPE_USINT	80\n", file = filename, append = TRUE)
    qqcat("OUTVAR		OUT_PRESSURE	* OUT_TYPE_USINT	100\n", file = filename, append = TRUE)
    qqcat("OUTVAR		OUT_REL_HUMID	* OUT_TYPE_USINT	100\n", file = filename, append = TRUE)
    qqcat("OUTVAR		OUT_WIND	* OUT_TYPE_USINT	100\n", file = filename, append = TRUE)
  }
  
}


